df = pd.DataFrame(data)

# Data Cleaning: Handle missing locations and empty termination dates
df['effective_end_date'] = df['effective_end_date'].replace('', pd.NaT)
df['location'] = df['location'].fillna('Unknown')

# Convert the 'effective_end_date' to datetime format
df['effective_end_date'] = pd.to_datetime(df['effective_end_date'], errors='coerce')

# Filter for active employees only (those with no termination date)
active_employees_df = df[df['employment_status'] == 'Active']

# Get the last year range
today = pd.Timestamp.now()
last_year = today - pd.DateOffset(years=1)

print(last_year)

# Filter for employees who were terminated within the last year
terminated_in_last_year = df[(df['effective_end_date'].notna()) & (df['effective_end_date'] >= last_year)]

# Create a column for the termination month using .loc to avoid SettingWithCopyWarning
terminated_in_last_year.loc[:, 'termination_month'] = terminated_in_last_year['effective_end_date'].dt.to_period('M')

# Ensure 'termination_month' is treated as a string for seaborn plotting
terminated_in_last_year['termination_month'] = terminated_in_last_year['termination_month'].astype(str)

# Count the number of terminations by location and month
termination_trends = terminated_in_last_year.groupby(['location', 'termination_month']).size().reset_index(name='termination_count')

# To calculate turnover, we need the count of active employees per month
# Assuming each person has been employed throughout the year for simplicity (since we don't have exact hire dates)

# Get the count of active employees per location for the last year
active_employees_by_month = active_employees_df.groupby(['location']).size().reset_index(name='employee_count')

# Merge termination trends with employee count to calculate turnover rate
termination_trends = termination_trends.merge(active_employees_by_month, on='location')

# Calculate the turnover rate (number of terminations / total active employees * 100)
termination_trends['turnover_rate'] = (termination_trends['termination_count'] / termination_trends['employee_count']) * 100

# Plot the termination and turnover trend by location over the last year
fig, ax1 = plt.subplots(figsize=(10, 6))

# Line plot for termination trend
sns.lineplot(data=termination_trends, x='termination_month', y='termination_count', hue='location', marker="o", ax=ax1)
ax1.set_ylabel('Number of Terminations')
ax1.set_title('Monthly Turnover and Termination Trend by Location (Last Year)')
ax1.set_xticklabels(termination_trends['termination_month'], rotation=45)

# Create a secondary axis to plot turnover rate
ax2 = ax1.twinx()
sns.lineplot(data=termination_trends, x='termination_month', y='turnover_rate', hue='location', marker="x", ax=ax2, legend=False, linestyle='--')
ax2.set_ylabel('Turnover Rate (%)')

# Customize the plot
ax1.legend(title='Location', loc='upper left')
plt.tight_layout()

# Show the plot
plt.show()
